// This is a class that extends another class called Parent

class Child extends Parent
{
    // This is an overridden method from the parent class that takes an integer parameter and prints it
    @Override
    void show(int p) { System.out.print(p+" "); }

    // This is an overridden method from the parent class that takes an integer parameter and prints its square
    @Override
    void print(int q){
        System.out.println(q*q);
    }
}
