// Back-end complete function Template for C++

class Solution {
  public:
    // Function to calculate the expected value
    // given the number of elements (N), number of groups (K), and the array (A)
    long long expectedValue(int N, int K, vector<int> A) {
        // Calculating the sum of all elements in the array
        long long sum = 0;
        for (auto u : A) sum += u;

        // Calculating the expected value using the formula
        long long ans = sum * N;
        ans /= K;

        // Returning the calculated expected value
        return ans;
    }
};

// Back-end complete function Template for Java

class Solution {
    // Function to calculate the expected value
    long expectedValue(int N, int K, int A[]) {
        // Initialize sum variable to 0
        long sum = 0;
        // Calculate the sum of first K elements in array A
        for (int i = 0; i < K; i++) sum += A[i];
        // Calculate the expected value by multiplying the sum with N and dividing by K
        long ans = sum * N;
        ans /= K;
        // Return the calculated expected value
        return ans;
    }
}

