//Back-end complete function Template for C++

int josephus(int n, int k) {
    list<int> l;

    for (int i = 1; i <= n; i++) {
        l.push_back(i);
    }

    auto it = l.begin();

    while (l.size() > 1) {
        for (int count = 1; count < k; count++) {
            it++;
            if (it == l.end()) {
                it = l.begin();
            }
        }
        it = l.erase(it);

        if (it == l.end())
            it = l.begin();
    }

    return *l.begin();
}

class Solution
{
    public int josephus(int n, int k)
    {
        List<int> l = new List<int>();

        for (int i = 1; i <= n; i++)
        {
            l.Add(i);
        }

        int index = 0;

        while (l.Count > 1)
        {
            index = (index + k - 1) % l.Count;
            l.RemoveAt(index);
        }

        return l[0];
    }
}
