class Solution {
  public:
    // Function to check if we can reach the last index from 0th index.
    bool canReach(vector<int> &arr) {
        int n = arr.size();

        // Initializing the current index to the last index.
        int cur = n - 1;

        // iterating from last index to 0th index.
        for (int i = n - 1; i >= 0; i--) {
            // checking if we can reach the current index from the ith index.
            if (i + arr[i] >= cur)
                cur = i; // updating the current index if true.
        }

        // returning 1 if we can reach the last index from 0th index,
        // otherwise returning 0.
        if (cur)
            return 0;
        else
            return 1;
    }
};

