class Solution{
	public:
		int KthDistinct(vector<int>nums,int k){
			int maxi = 0;
			for(int i = 0; i < nums.size(); i++)
				maxi = max(maxi, nums[i]);
			vector<int>hash(maxi+1, 0);
			for(int i = 0; i < nums.size(); i++)
				hash[nums[i]]++;
			int cnt = 0;
			for(int i = 0; i < nums.size(); i++){
				if(hash[nums[i]] == 1)
					cnt++;
				if(cnt == k)
					return nums[i];
			}
			return -1;
		}
};

class Solution
{
    //Complete this function
    //Function to find the Kth distinct element in the array.
    public int KthDistinct(List<int> nums, int k)
    {
        int maxi = 0;
        for (int i = 0; i < nums.Count; i++)
        {
            maxi = Math.Max(maxi, nums[i]);
        }
        var hash = new int[maxi + 1];
        for (int i = 0; i < nums.Count; i++)
        {
            hash[nums[i]]++;
        }
        int cnt = 0;
        for (int i = 0; i < nums.Count; i++)
        {
            if (hash[nums[i]] == 1)
            {
                cnt++;
            }
            if (cnt == k)
            {
                return nums[i];
            }
        }
        return -1;
    }
}
