class Solution
{
    public string printNumber(string s, int n)
    {
        StringBuilder temp = new StringBuilder();

        for (int i = 0; i < n; i++)
        {
            switch (s[i])
            {
                case 'a':
                case 'b':
                case 'c':
                    temp.Append("2");
                    break;
                case 'd':
                case 'e':
                case 'f':
                    temp.Append("3");
                    break;
                case 'g':
                case 'h':
                case 'i':
                    temp.Append("4");
                    break;
                case 'j':
                case 'k':
                case 'l':
                    temp.Append("5");
                    break;
                case 'm':
                case 'n':
                case 'o':
                    temp.Append("6");
                    break;
                case 'p':
                case 'q':
                case 'r':
                case 's':
                    temp.Append("7");
                    break;
                case 't':
                case 'u':
                case 'v':
                    temp.Append("8");
                    break;
                default:
                    temp.Append("9");
                    break;
            }
        }

        return temp.ToString();
    }
}

