class Solution {
  public:
    // Function to solve 0/1 knapsack problem
    int knapSack(vector<int>& val, vector<int>& wt, int capacity) {
        int N = val.size();
        // Creating a vector to store maximum values for each weight
        vector<int> dp(capacity + 1, 0);
        int ans = 0;

        // looping through each weight
        for (int i = 0; i < capacity + 1; i++)
            // looping through each item
            for (int j = 0; j < N; j++)
                // checking if current weight is less than or equal to the maximum
                // weight
                if (wt[j] <= i)
                    // updating the maximum value for the current weight
                    dp[i] = max(dp[i], dp[i - wt[j]] + val[j]);
        // returning the maximum value for the maximum weight
        return dp[capacity];
    }
};

