class Solution {
    // Function to solve 0/1 knapsack problem
    public int knapSack(List<int> val, List<int> wt, int capacity) {
        int N = val.Count;
        // Creating an array to store maximum values for each weight
        int[] dp = new int[capacity + 1];

        // Looping through each weight
        for (int i = 0; i <= capacity; i++) {
            // Looping through each item
            for (int j = 0; j < N; j++) {
                // Checking if current weight is less than or equal to the maximum
                // weight
                if (wt[j] <= i) {
                    // Updating the maximum value for the current weight
                    dp[i] = Math.Max(dp[i], dp[i - wt[j]] + val[j]);
                }
            }
        }
        // Returning the maximum value for the maximum weight
        return dp[capacity];
    }
}

