class Solution {
  public:

    // helper method to get Factorial of a Number
    int factorial(int n)
    {
        int fact = 1;
        while (n != 0)
        {
            fact = fact*n;
            n--;
        }
        return fact;
    }

    string isKrishnamurthy(int N) {

        int res = 0;

        int dup = N;

        while(dup)
        {
            res+=factorial(dup%10);     // Adding Factorial of every number to res
            dup/=10;
        }

        // If res equals to N, then it's a Krishnamurthy Number, else not.
        if(res==N)
            return "YES";
        else
            return "NO";
    }
};

