    isKrishnamurthy(n){
        let res = 0;
        let dup = n;
        
        // function to get factorial of a number
        function factorial(num){
            let fact = 1;
            while(num>0){
                fact = fact*num;
                num--;
            }
            return fact;
        }
        
        // calculate the sum of factorial of digits
        while(dup>0){
            res+= factorial(dup%10);
            dup = Math.floor(dup/10);
        }
        
        // check if the sum is equal to the original number N
        if(res===n){
            return "YES";
        }
        else{
            return "NO";
        }
    }
}

