//Back-end complete function template for C++

class Solution{
public:
	//Function to find the value of the kth least significant bit of n.
	int KthLSB(long long int n, int k){

	    //Bitwise AND of n and (1<<k-1) to check if the kth bit is 1 or 0.
	    if(n & (1LL << (k - 1))) return 1;

	    //Return 0 if the kth bit is 0.
	    return 0;
	}
};

//User function Template for C#

class Solution
{
    //Complete this function
    public int KthLSB(long n, int k)
    {
        //Bitwise AND of n and (1LL<<k-1) to check if the kth bit is 1 or 0.
        if ((n & (1LL << (k - 1))) != 0) return 1;

        //Return 0 if the kth bit is 0.
        return 0;
    }
}
