//Back-end complete function template for C++

class Solution{
public:
    vector<long long> factorial(vector<long long> a, int n) {
        int i, MAX = 1e5;
        long long fac[MAX + 1], mod = 1e9 + 7;
        fac[0] = 1;
        for (i = 1; i <= MAX; i++) {
            fac[i] = (fac[i - 1] % mod * (long long)i) % mod;
        }
        
        vector<long long> ans(n);
        for (i = 0; i < n; i++) {
            ans[i] = fac[a[i]];
        }
        return ans;
    }
};

// Back-end complete function Template for Java

class Solution {
    public long[] factorial(long a[], int n) {
        // initialize variables
        int i, MAX = 100_000;
        long fac[] = new long[MAX + 1], mod = 1000_000_007;
        
        fac[0] = 1; // initialize factorial of 0 as 1
        
        // calculate factorial for each number up to MAX
        for (i = 1; i <= MAX; i++) {
            fac[i] = (fac[i - 1] % mod * (long)i) % mod;
        }
        
        long[] ans = new long[n]; // store factorial values
        
        // store factorial values for input array elements in ans array
        for (i = 0; i < n; i++) {
            ans[i] = fac[(int)a[i]];
        }
        return ans;
    }
}

class Solution {
    factorial(a, n) {
        let i, MAX = 100000; // Declare a variable MAX to store the maximum value.
        let fac = new Array(MAX + 1), mod = 1000000007; // Create an array fac to store factorial values and a variable mod to store the modulus value.
        fac[0] = 1; // Assign the first element of fac as 1.
        for (i = 1; i <= MAX; i++) {
            fac[i] = (fac[i - 1] % mod * i) % mod; // Calculate the factorial values using the previous value in the array.
        }
        let ans = new Array(n); // Create an array ans to store the factorial values for the given array.

        for (i = 0; i < n; i++) {
            ans[i] = fac[a[i]]; // Assign the factorial value for each element in the given array to the corresponding index in ans.
        }
        return ans; // Return the array containing the factorial values.
    }
}
