class Solution {
  public:
    int largestPrimeFactor(int n) {
        int num = 2;
        // loop till sqrt(n)
        while ((num * num) <= n) {
            if (n % num == 0) // if num divides n evenly
                n /= num;     // divide n by num
            else
                num++; // increase num if it doesn't divide n
        }
        return n;
    }
};

