class Solution {
  public:
    vector<int> dupLastIndex(vector<int>& arr) {
        int n = arr.size();
        for (int i = n - 1; i > 0; i--) {
            if (arr[i] == arr[i - 1]) {
                return {i, arr[i]}; // Return index and value of the last duplicate
            }
        }
        return {-1, -1}; // If no duplicate is found, return -1, -1
    }
};

