class Solution:
    #Function to find the last index of a given character in a string.
    def LastIndex(self, s, p):
        #Iterating the string in reverse order.
        for i in range(len(s)-1,-1,-1):
            #Checking if the current character is equal to the given character.
            if s[i]==p:
                #Returning the index if match is found.
                return i
        #Returning -1 if no match is found.
        return -1

