    // Function to find the last digit of the Fibonacci number
    int fibonacciDigits(long long N){
        int fib[300];
        fib[0]=0;
        fib[1]=1;
        // Finding Fibonacci numbers modulo 100 and storing in an array
        for(int i=2;i<=min(299LL,N);i++)
         fib[i]=(fib[i-1]+fib[i-2])%100;
