class Solution{
    // Function to calculate the last 2 digits of the Fibonacci number at position N
    int fibonacciDigits(long N){
        // Declare an array to store the Fibonacci numbers
        int fib[]=new int[300];
        // Initialize the first two Fibonacci numbers
        fib[0]=0;
        fib[1]=1;
        // Calculate the Fibonacci numbers up to position N or until position 299
        for(int i=2;i<=Math.min(299,N);i++)
            // Calculate the next Fibonacci number using the sum of the previous two numbers
            fib[i]=(fib[i-1]+fib[i-2])%100;
        // Return the Fibonacci number at position N modulo 100 (last 2 digits)
        return fib[(int)(N%300)]; 
    }
}

class Solution{
    public int fibonacciDigits(long N){
        int[] fib = new int[300];
        fib[0] = 0;
        fib[1] = 1;

        for(int i=2;i<=Math.Min(299,N);i++)
            fib[i] = (fib[i-1] + fib[i-2])%100;

        return fib[(N%300)];
    }
