#include <stdio.h>

int main() {
    int a, b;
    scanf("%d %d", &a, &b);
    // code here
    int x = (a > b) ? a : b; // Using ternary operator to find maximum
    int y = a * b;
    int ans = x;

    for (int i = x; i <= y; i++) {
        if (i % a == 0 && i % b == 0) {
            ans = i;
            break;
        }
    }

    printf("%d\n", ans);

    return 0;
}

