class Solution {
    lcmAndGcd(a, b) {
        // Define a helper function to calculate the greatest common divisor (GCD) of
        // two numbers.
        const gcd =
            (a, b) => {
                if (a === 0) {
                    return b;
                }
                return gcd(b % a, a);
            }

        const arr = []; // Initialize an array to store the calculated LCM and GCD.

        const g = gcd(a, b);   // Calculate the GCD of the given numbers.
        const l = (a * b) / g; // Calculate the LCM of the given numbers using the
                               // formula: LCM = (a * b) / GCD.

        arr.push(l); // Add the calculated LCM to the array.
        arr.push(g); // Add the calculated GCD to the array.

        return arr; // Return the array with the calculated LCM and GCD.
    }
}
