//Back-end complete function Template for C++

int LCM(int a, int b){

    int x = max(a, b);
    int y = a * b;
    int ans = x;
    for(int i = x; i <= y; i++){
        if(i % a == 0 && i % b == 0){
            ans = i;
            break;
        }
    }
    return ans;
}

