#include <stdio.h>

int main() {
    // code here
    int year;
    scanf("%d", &year);

    if ((year % 4 == 0 && year % 100 != 0) || (year % 400 == 0)) {
        printf("True\n");
    } else {
        printf("False\n");
    }

    return 0;
}

