int checkYear(int N) {
    // if year is divisible by 100
    if (N % 100 == 0) {
        // then the year should be divisible by 400 to be a leap year
        if (N % 400 == 0) {
            return 1;
        } else
            return 0;
    }
    // if number is not divisible by 100 then to be a leap year
    // it should br divisible by 4
    else if (N % 4 == 0)
        return 1;
    else
        return 0;
}

