//Back-end complete function template in C++

class Solution{
	public:

	//Function to find the leftmost digit of the product of all elements in the given array.
	int LeftMostDigit(vector<int> nums)
	{
		double S = 0; //variable to store the sum of logarithms of array elements
        int n = nums.size();

        //calculating the sum of logarithms of all elements in the array
        for (int i = 0; i < n; i++)
            S = S + log10(nums[i] * 1.0);

        //calculating the fractional part of S
        double fract_S = S - floor(S);

        //taking 10 to the power of the fractional part to get the leftmost digit
        int ans = pow(10, fract_S);
        return ans; //returning the leftmost digit
	}
};
