 * @param {number} k
 * @returns {number[]}
 */

class Solution {
    lessThan(arr, k) {
        let ans = [];

        // write your code below to append all numbers to ans which are less than k
        for (let i = 0; i < arr.length; i++) {
            if (arr[i] < k) {
                ans.push(arr[i]);
            }
        }

        return ans;
    }
}
