class Solution{
    // Utility function to find the GCD  
    // of two numbers 
    static int gcd(int a, int b) 
    { 
        return (a % b == 0) ?  
                Math.abs(b) : gcd(b,a%b); 
    }
    static int isPossible(int A,int B,int C){ 
        // C is divisible by GCD(A,B) then return 1 or return 0;
        return (C % gcd(A, B) == 0 ? 1:0);
    } 
}

