class Solution
{
    //Complete this function
    //Function to check if it is possible to measure exactly C litres using the jugs of A litres and B litres.
    public int isPossible(int A, int B, int C)
    {
        return (C % gcd(A, B) == 0) ? 1 : 0;
    }

    // Function to find the greatest common divisor of two numbers
    private int gcd(int a, int b)
    {
        if (b == 0)
        {
            return a;
        }
        else
        {
            return gcd(b, a % b);
        }
    }
}
