public static LinkedList<Integer> insertion(int arr[]) {

    // Creating a new LinkedList of type Integer
    LinkedList<Integer> ll = new LinkedList<>();

    // adding all the elements of the ArrayList to the LinkedList
    for (int i : arr) ll.add(i);

    // returning the LinkedList
    return ll;
}
