//Back-end complete function Template for C++
class Solution{   
public:
    //Function to calculate the total money
    long long totalMoney(long long N){

        long long rem=3;
        //Loop until N is greater than rem, subtracting rem from N and
        //multiplying rem by 2 in each iteration.
        while(N>rem)
        {
            N=N-rem;
            rem*=2;
        }

        //Returning the remaining amount of money.
        return rem-N+1;

    }
};

class Solution{
    // Function to calculate the total money
    // Takes in an input N
    static Long totalMoney(Long N){
        // Initialize the remainder with 3
        long rem=3;
        // While N is greater than the remainder
        while(N>rem)
        {
            // Subtract the remainder from N
            N=N-rem;
            // Multiply the remainder by 2
            rem*=2;
        }

        // Calculate the final result as remainder minus N plus 1
        return rem-N+1;
    }
}

//User function Template for C#

class Solution
{
    //Complete this function
    public long totalMoney(long N)
    {
        long rem=3;
        //Loop until N is greater than rem, subtracting rem from N and
        //multiplying rem by 2 in each iteration.
        while(N > rem)
        {
            N = N - rem;
            rem *= 2;
        }

        //Returning the remaining amount of money.
        return rem - N + 1;
    }
}
