// Back-end complete function Template for C++

class Solution {
  public:
    //Function to calculate the minimum number of operations to perform.
    long long int minOperations(int N) {
        //calculating the answer by dividing N by 2 and 
        //multiplying it with the next integer (N+1) divided by 2.
        long long int ans = N / 2;
        ans *= (N + 1) / 2;
        //returning the final answer.
        return ans;
    }
};

