//Back-end complete function Template for C

int findMinOperation(int n, int matrix[][n]) {
    int ans = 0, maxi = 0;
    int rowSum[n], colSum[n];
    // Row sums and finding maximum row sum
    for (int i = 0; i < n; i++) {
        int sum = 0;
        for (int j = 0; j < n; j++) {
            sum += matrix[i][j];
        }
        rowSum[i] = sum;
        maxi = (maxi > rowSum[i]) ? maxi : rowSum[i];
    }

    // Column sums and finding maximum column sum
    for (int j = 0; j < n; j++) {
        int sum = 0;
        for (int i = 0; i < n; i++) {
            sum += matrix[i][j];
        }
        colSum[j] = sum;
        maxi = (maxi > colSum[j]) ? maxi : colSum[j];
    }

    // Calculating the minimum operations
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            int minAdd = (maxi - rowSum[i]) < (maxi - colSum[j]) ? (maxi - rowSum[i])
                                                                 : (maxi - colSum[j]);
            ans += minAdd;
            rowSum[i] += minAdd;
            colSum[j] += minAdd;
        }
    }

    return ans;
}

class Solution {
  public:
    // Function to find minimum number of operations that are required
    // to make the matrix beautiful.
    int findMinOperation(vector<vector<int> >& mat) {
        int i, j, n = mat.size();
        int ans = 0, maxi = 0;
        vector<int> rowSum(n, 0), colSum(n, 0);
        for (int i = 0; i < n; i++) {
            int sum = 0;
            for (int j = 0; j < n; j++) {
                sum += mat[i][j];
            }
            rowSum[i] = sum;
            maxi = max(maxi, rowSum[i]);
        }
        for (int j = 0; j < n; j++) {
            int sum = 0;
            for (int i = 0; i < n; i++) {
                sum += mat[i][j];
            }
            colSum[i] = sum;
            maxi = max(maxi, colSum[i]);
        }
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                // finding minimum increment required in either row or column.
                int minAdd = min(maxi - rowSum[i], maxi - colSum[j]);
                ans += minAdd;
                rowSum[i] += minAdd;
                colSum[j] += minAdd;
            }
        }
        return ans;
    }
};

class Solution {
