public class Solution {
    // Function to find minimum number of operations that are required
    // to make the matrix beautiful.
    public int findMinOperation(int[][] mat) {
        int n = mat.Length;
        int res = 0;
        int maxSum = 0;

        // Find maximum sum across all rows
        for (int i = 0; i < n; i++) {
            int sum = 0;
            for (int j = 0; j < n; j++) sum += mat[i][j];
            maxSum = Math.Max(sum, maxSum);
        }

        // Find maximum sum across all columns
        for (int j = 0; j < n; j++) {
            int sum = 0;
            for (int i = 0; i < n; i++) sum += mat[i][j];
            maxSum = Math.Max(sum, maxSum);
        }

        // Sum of operations across all rows
        for (int i = 0; i < n; i++) {
            int sum = 0;
            for (int j = 0; j < n; j++) sum += mat[i][j];
            res += (maxSum - sum);
        }
        return res;
    }
}

class Solution {
    findMinOperation(mat) {
        let n = mat.length;
        let ans = 0, maxi = 0;
        let rowSum = new Array(n).fill(0);
        let colSum = new Array(n).fill(0);

        // Row sums and finding maximum row sum
        for (let i = 0; i < n; i++) {
            rowSum[i] = mat[i].reduce((sum, val) => sum + val, 0);
            maxi = Math.max(maxi, rowSum[i]);
        }

        // Column sums and finding maximum column sum
        for (let j = 0; j < n; j++) {
            colSum[j] = mat.reduce((sum, row) => sum + row[j], 0);
            maxi = Math.max(maxi, colSum[j]);
        }

        // Calculating the minimum operations
        for (let i = 0; i < n; i++) {
            for (let j = 0; j < n; j++) {
                let minAdd = Math.min(maxi - rowSum[i], maxi - colSum[j]);
                ans += minAdd;
                rowSum[i] += minAdd;
                colSum[j] += minAdd;
            }
        }

        return ans;
    }
}
