class Solution
{
public:
    //Function to make a number odd by finding the largest power of 2 that divides it.
    int makeNumberOdd(int N)
    {
        int c=1;
        if(N%2==0)
        {
            //finding the largest power of 2 that divides N
            while(N%2==0&&N>0)
            {
                //multiplying c by 2 and dividing N by 2
                c*=2;
                N=N/2;
            }

        }
        //returning the largest power of 2 that divides N
        return c;
    }
};

