//Back-end complete function Template for C++

class Solution {
  public:
    long long powerGame(long long X, long N) {
        
        long long ans = 0;   // Initializing ans as 0
        
        long long d = X;    // Creating a duplicate of original X
        
        for(int i=1 ; i<=N ; i++)
        {
            long long dup = X;
            long long c = dup%10;    // Last digit of (X power i)
            // Loop to get the First digit of (X power i)
            while(dup>=10)
                dup/=10;
            
            ans+=dup;
            ans*=10;
            ans+=c;
            ans*=10;
            
            X *= d;    // Changing X to it's next power
        }
        
        ans/=10;
        
        return ans;
    }
};

