class Solution {
public:
	int CommonFactor(int n){
		vector<bool>prime(n+5, true);
		prime[0] = prime[1] = false;
		for(int i = 2; i*i <= n; i++){
			if(prime[i]){
				for(int j = i*i; j <= n; j+=i)
					prime[j] = false;
			}
		}
		vector<int>etf(n+5, 0);
	    etf[1]=1;
	    for(int i = 2; i <= n; i++){
	        if(prime[i]) etf[i] = i-1;
	        else etf[i] = i;
	    }
	    for(int i = 2; i <= n;i++)
	        if(prime[i]){
	        for(int j = 2*i; j <= n; j+=i)
	            etf[j] -= etf[j]/i;
	        }
	    return n - etf[n];
	}

};

