class Solution
{
    //Complete this function
    public int CommonFactor(int n)
    {
        bool[] prime = new bool[n + 5];
        int[] etf = new int[n + 5];

        for (int i = 0; i < prime.Length; i++)
        {
            prime[i] = true;
        }

        prime[0] = prime[1] = false;

        for (int i = 2; i * i <= n; i++)
        {
            if (prime[i])
            {
                for (int j = i * i; j <= n; j += i)
                {
                    prime[j] = false;
                }
            }
        }

        etf[1] = 1;

        for (int i = 2; i <= n; i++)
        {
            if (prime[i]) 
                etf[i] = i - 1;
            else 
                etf[i] = i;
        }

        for (int i = 2; i <= n; i++)
        {
            if (prime[i])
            {
                for (int j = 2 * i; j <= n; j += i)
                {
                    etf[j] -= etf[j] / i;
                }
            }
        }

        return n - etf[n];
    }
}
