class Solution {
  public:
    // Function to find maximum value among the difference of element and index.
    int maxVal(vector<int> &arr) {
        // Initializing min and max variables with maximum and minimum values.
        int minVal = INT_MAX, maxVal = INT_MIN;

        // Iterating over the array.
        for (int i = 0; i < arr.size(); i++) {
            // Finding the maximum difference between element and index.
            if ((arr[i] - i) > maxVal)
                maxVal = arr[i] - i;
            // Finding the minimum difference between element and index.
            if ((arr[i] - i) < minVal)
                minVal = arr[i] - i;
        }

        // Calculating and returning the maximum difference.
        return (maxVal - minVal);
    }
};

java.full_func:
class Solution {
    // Function to find maximum value among the difference of element and index.
    public int maxVal(List<Integer> arr) {
        int minVal = Integer.MAX_VALUE;
        int maxVal = Integer.MIN_VALUE;

        // Iterating over the array.
        for (int i = 0; i < arr.size(); i++) {
            int diff = arr.get(i) - i;
            // Finding the maximum difference between element and index.
            if (diff > maxVal) maxVal = diff;
            // Finding the minimum difference between element and index.
            if (diff < minVal) minVal = diff;
        }

        // Calculating and returning the maximum difference.
        return maxVal - minVal;
    }
}

