class Solution:

    def maxVal(self, arr):
        min_val = float('inf')
        max_val = float('-inf')

        # Iterating over the array.
        for i in range(len(arr)):
            diff = arr[i] - i
            # Finding the maximum difference between element and index.
            if diff > max_val:
                max_val = diff
            # Finding the minimum difference between element and index.
            if diff < min_val:
                min_val = diff

        # Calculating and returning the maximum difference.
        return max_val - min_val

