// User function Template for JavaScript
/**
 * @param {number[]} arr
 * @returns {number}
 */

class Solution {
    // Function to find the maximum value difference between element and index.
    maxVal(arr) {
        let n = arr.length;
        let min = Number.POSITIVE_INFINITY;
        let max = Number.NEGATIVE_INFINITY;

        // Iterating over the array.
        for (let i = 0; i < n; i++) {
            let diff = arr[i] - i;
            // Updating max if the current difference is greater.
            if (diff > max) {
                max = diff;
            }
            // Updating min if the current difference is smaller.
            if (diff < min) {
                min = diff;
            }
        }
        // Returning the difference between max and min.
        return max - min;
    }
}
