class Solution {
  public:
    // k is the maximum number of zeros allowed to flip
    int maxOnes(vector<int>& arr, int k) {
        int maxLen = 0, l = 0, zeros = 0;

        // iterate over the array
        for (int r = 0; r < arr.size(); r++) {
            // count zeros
            if (arr[r] == 0)
                zeros++;

            // adjust window size
            while (zeros > k) {
                if (arr[l] == 0)
                    zeros--;
                l++;
            }

            // update max length
            maxLen = max(maxLen, r - l + 1);
        }

        return maxLen;
    }
};

class Solution {
    // k is the maximum number of zeros allowed to flip
    public int maxOnes(int[] arr, int k) {
        // maxLen stores the size of the largest window
        int maxLen = 0;
        // l is the left pointer of the sliding window
        int l = 0;
        // zeros counts the number of zeros in the current window
        int zeros = 0;

        // Iterate through the array using the right pointer r
        for (int r = 0; r < arr.length; r++) {
            // If the current element is zero, increment zeros
            if (arr[r] == 0) zeros++;

            // If the number of zeros exceeds k, move the left pointer to shrink the
            // window
            while (zeros > k) {
                // If the leftmost element is zero, decrement zeros
                if (arr[l] == 0) zeros--;
                // Move the left pointer to the right
                l++;
            }

            // Update maxLen to the size of the current valid window
            maxLen = Math.max(maxLen, r - l + 1);
        }

        // Return the maximum size of the window found
        return maxLen;
    }
}

class Solution {
    maxOnes(arr, k) {
        let maxLen = 0; // Initialize variable to store maximum length of subarray with
                        // at most k zeros
        let l = 0;      // Initialize left pointer for sliding window
        let zeros = 0; // Initialize variable to count number of zeros in current window

        for (let r = 0; r < arr.length;
             r++) {             // Iterate over the array using right pointer
            if (arr[r] === 0) { // If current element is zero, increment zeros count
                zeros++;
            }

            while (zeros > k) { // While zeros count exceeds k, move left pointer and
                                // decrease zeros count
                if (arr[l] === 0) {
                    zeros--;
                }
                l++;
            }

            maxLen = Math.max(
                maxLen, r - l + 1); // Update maxLen with maximum length found so far
        }

        return maxLen; // Return the maximum length of subarray with at most k zeros
    }
}
