class Solution {
public:
    int  maximize_xor_count(int n ) 
    {
    	int cnt = 1;
    	int ans = 0;
    	while(n > 0)
    	{
    		// If unset bit found add the cnt to the answer
    		if(n % 2 == 0)
    			ans += cnt;
    		// Multiply cnt by 2
    		cnt *= 2;
    		// Proceed for next bit.
    		n = n / 2;
    	} 
    	return ans;
    }
};
