//Back-end complete function Template for C++

vector<int> maximum_from_left(vector<int>& arr) {

    int n;
    n = arr.size();
    int maxi;
    maxi = arr[0];
    vector<int> result;

    for (int i = 1; i < n; i++) {
        if (arr[i] > maxi) {
            result.push_back(i);
        }
        maxi = max(maxi, arr[i]);
    }

    return result;
}

//User function Template for C#

class Solution
{
    //Complete this function
    //Function to find the maximum number from left for every index i.
    public List<int> maximum_from_left(List<int> v)
    {
        int n = v.Count;
        int maxi = v[0];
        List<int> result = new List<int>();

        for(int i=1; i<n; i++)
        {
            if(v[i] > maxi)
            {
                result.Add(i);
            }
            maxi = Math.Max(maxi, v[i]);
        }

        return result;
    }
}
