cpp.full_func:
class Solution {
  public:
    int maxStairHeight(int N) {

        // Calculating the required answer.
        int ans = (int)(sqrt(2.0 * N + 0.25) - 0.5);

        return ans;
    }
};

java.full_func:
//Back-end complete function template for Java

class Solution {
    static int maxStairHeight(int N) {

        // Calculating the required answer.
        int ans = (int)(Math.sqrt(2.0 * N + 0.25) - 0.5);

        // Returning the result as the maximum stair height.
        return ans;
    }
};

javascript.full_func:
class Solution {
  maxStairHeight(N) {
    // Calculating the required answer.
    const answer = Math.floor(Math.sqrt(2 * N + 0.25) - 0.5);

    return answer;
  }
}
