//Back-end complete function Template for C++

class Solution{
public:
    // Function to calculate the height of the pyramid
    int height(int N){
        // Calculating the maximum possible height of the pyramid using the formula x = sqrt(2*N)
        int x = sqrt(2*N);

        // Checking if the actual number of blocks is less than the maximum possible number of blocks in the pyramid
        // If it is less, then reducing the height by 1 to get the actual height of the pyramid
        if(N < (x*(x + 1))/2)
            x--;

        // Returning the height of the pyramid
        return x;
    }
};

// Back-end complete function Template for java

class Solution{
    // Function to calculate the height of the given integer
    static int height(int N){
        // Calculate the maximum possible height using the formula
        int x = (int)Math.sqrt(2*N);
        // Check if the actual height is less than the calculated maximum height
        if(N < (x*(x + 1))/2)
            x--;
        // return the maximum height
        return x;
    }
}

