//Back-end complete function Template for C
//  Function to find the maximum height in inches
int findMax(struct Height arr[], int n) {
    int mx = INT_MIN;

    // Iterating through the array to find the maximum height in inches
    for (int i = 0; i < n; i++) {
        int temp = 12 * arr[i].feet + arr[i].inches;
        if (temp > mx) {
            mx = temp;
        }
    }

    return mx;
}

class Solution {
  public:
    int findMax(vector<Height>& arr) {
        // Initializing the maximum value as the minimum possible value
        int mx = INT_MIN;

        // Iterating through the vector to find the maximum height in terms of inches
        for (const auto& h : arr) {
            // Converting the height from feet and inches format to inches
            int temp = 12 * (h.feet) + h.inches;

            // Updating the maximum value if the current height is greater
            mx = max(mx, temp);
        }

        // Returning the maximum height in terms of inches
        return mx;
    }
};

class Solution {
    // Function to find the maximum height in terms of inches
    public int findMax(ArrayList<Height> arr) {
        // Initialize the maximum value as the minimum possible value
        int max = Integer.MIN_VALUE;

        // Iterate through the ArrayList to find the maximum height in inches
        for (Height h : arr) {
            // Convert the height from feet and inches to inches
            int temp = 12 * h.feet + h.inches;

            // Update the maximum value if the current height is greater
            if (temp > max) {
                max = temp;
            }
        }

        // Return the maximum height in inches
        return max;
    }
}

//Back-end complete function Template for javascript
class Solution {
    findMax(arr) {
        let maxInches =
            Number.NEGATIVE_INFINITY; // Initialize to the smallest possible value

        // Iterate through the list to find the maximum height in terms of inches
        for (const h of arr) {
            // Convert the height from feet and inches to inches
            const totalInches = 12 * h.feet + h.inches;

            // Update the maximum value if the current height is greater
            maxInches = Math.max(maxInches, totalInches);
        }

        // Return the maximum height in terms of inches
        return maxInches;
    }
}
