// Back-end complete function Template for C++
class Solution{
public:
    // Function to find the maximum sum of numbers from 1 to N
    int maxSum(int N){
        // If N is 1, return N
        if(N == 1)
            return N;

        // Calculate the sum using the formula
        int x = (N *(N-1))/2 - 1 + N/2;

        // Return the maximum sum
        return x;
    }
};

