// Back-end complete function Template for Java

class Solution{
    // Function to calculate the maximum sum
    static int maxSum(int N){
        // If N is equal to 1, return 1
        if(N == 1)
            return N;

        // Calculate the maximum sum using the given formula
        int x = (N*(N-1))/2 - 1 + N/2;

        // Return the maximum sum
        return x;
    }
}
