//Back-end complete function Template for C++

class Solution {
  public:
    int maxValue(vector<int>& arr) {
        int n = arr.size();
        vector<int> a(n), b(n);

        // Calculating first_array and second_array
        for (int i = 0; i < n; i++) {
            a[i] = arr[i] + i;
            b[i] = arr[i] - i;
        }

        int max_a = *max_element(a.begin(), a.end());
        int min_a = *min_element(a.begin(), a.end());
        int ans1 = max_a - min_a;

        int max_b = *max_element(b.begin(), b.end());
        int min_b = *min_element(b.begin(), b.end());
        int ans2 = max_b - min_b;

        return max(ans1, ans2);
    }
};

class Solution {
    public int maxValue(int arr[]) {
        int n = arr.length;
        int[] a = new int[n];
        int[] b = new int[n];

        // Calculating first_array and second_array
        for (int i = 0; i < n; i++) {
            a[i] = arr[i] + i;
            b[i] = arr[i] - i;
        }

        int max_a = a[0], min_a = a[0];

        // Finding maximum and minimum value in first_array
        for (int i = 1; i < n; i++) {
            if (a[i] > max_a) max_a = a[i];
            if (a[i] < min_a) min_a = a[i];
        }

        int ans1 = max_a - min_a;

        int max_b = b[0], min_b = b[0];

        // Finding maximum and minimum value in second_array
        for (int i = 1; i < n; i++) {
            if (b[i] > max_b) max_b = b[i];
            if (b[i] < min_b) min_b = b[i];
        }

        int ans2 = max_b - min_b;

        return Math.max(ans1, ans2);
    }
}

// User function Template for JavaScript

/**
 * @param {Number[]} arr
 * @returns {Number}
 */

class Solution {
    maxValue(arr) {
        let n = arr.length;
        let a = [], b = [];

        // Calculating arrays a[i] + i and a[i] - i
        for (let i = 0; i < n; i++) {
            a.push(arr[i] + i);
            b.push(arr[i] - i);
        }

        // Finding the maximum and minimum values in array a
        let maxA = Math.max(...a);
        let minA = Math.min(...a);
        let ans1 = maxA - minA;

        // Finding the maximum and minimum values in array b
        let maxB = Math.max(...b);
        let minB = Math.min(...b);
        let ans2 = maxB - minB;

        // Return the maximum difference
        return Math.max(ans1, ans2);
    }
}
