class Solution {
  public:
    //Function to get the volume of a rectangular prism.
    long long getVol(int A, int B) {

        //converting the dimensions to long double for more precise calculations.
        long double p = (long double)A;
        long double s = (long double)B;

        //calculating the volume using the given formula.
        long double v = (36*p*s - pow(p, 3) + sqrt(pow((p*p - 24*s), 3))) / (12*72);

        //converting the volume to long long and storing in ans.
        long long ans = (long long)v;

        //if the volume is negative, setting it to 0.
        if (ans<0)
            ans=0;

        //returning the volume.
        return ans;
    }
};

