class node {
  public:
    node *left;  // 0
    node *right; // 1
};
class trie {
    node *root;

  public:
    trie() { root = new node(); }
    void insert(int n) {
        node *temp = root;
        for (int i = 31; i >= 0; i--) {
            int bit = (n >> i) & 1;
            if (bit == 0) {
                if (temp->left == NULL) {
                    temp->left = new node();
                }
                temp = temp->left;
            } else {
                if (temp->right == NULL) {
                    temp->right = new node();
                }
                temp = temp->right;
            }
        }
    }
    long long max_XOR_helper(int value) {
        long long current_ans = 0;
        node *temp = root;
        for (int j = 31; j >= 0; j--) {
            int bit = (value >> j) & 1;
            if (bit == 0) {
                // find opp value
                if (temp->right != NULL) {
                    temp = temp->right;
                    current_ans += (1 << j);
                } else {
                    temp = temp->left;
                }
            } else {
                // look for a zero
                if (temp->left != NULL) {
                    temp = temp->left;
                    current_ans += (1 << j);
                } else {
                    temp = temp->right;
                }
            }
        }
        return current_ans;
    }
};
class Solution {
  public:
    vector<long long> maximumXor(int N, int Q, vector<int> &arr,
                                 vector<vector<int>> &queries) {
        vector<long long> ans(Q);
        vector<array<int, 3>> qry;
        sort(arr.begin(), arr.end());
        for (int i = 0; i < Q; i++) {
            qry.push_back({queries[i][1], queries[i][0], i});
        }
        sort(qry.begin(), qry.end());
        int j = 0;
        trie tree;
        for (int i = 0; i < Q; i++) {
            while (j < arr.size() and arr[j] <= qry[i][0]) {
                tree.insert(arr[j++]);
            }
            if (j == 0)
                ans[qry[i][2]] = -1;
            else
                ans[qry[i][2]] = tree.max_XOR_helper(qry[i][1]);
        }
        return ans;
    }
};

