from bisect import bisect_left, bisect_right
class Solution:
    def maximumXor(self, N, Q, nums, queries):
        nums.sort()
        answer = []
        for x, m in queries:
            start, stop = 0, bisect_right(nums, m)
            num = 0
            for i in range(30)[::-1]:
                cut = bisect_left(nums, num + (1<<i), start, stop)
                if cut > start and (x & (1<<i)):
                    stop = cut
                elif cut < stop:
                    start = cut
                    num += (1<<i)
            answer.append(num ^ x if start < stop else -1)
        return answer

